mapper.select:
		SELECT c_no, major_no, sub_no, mem_id, c_name, c_loc, c_place, c_date, c_time, c_price, c_count, c_image, c_detail, c_hits
  		FROM club
  		WHERE  major_no = #{major_no}

SELECT * 
    FROM (
        SELECT ROWNUM RN, A.* 
            FROM (
                    SELECT * 
                    FROM CLUB 
                    WHERE major_no = #{major_no}
                    ORDER BY C_NO DESC 
                    ) A
            )
WHERE RN BETWEEN #{start} AND #{end}
